import React, { useState, useEffect } from 'react';
import { 
  Box, Typography, TextField, Button, Select, MenuItem, 
  CircularProgress, Card, CardContent, Grid, Paper, Chip, Divider 
} from '@mui/material';
import axios from 'axios';

const niches = [
  'fashion', 'fitness', 'finance', 'food', 'travel', 
  'lifestyle', 'technology', 'education', 'health', 'beauty'
];

const ContentIdeaGenerator = () => {
  const [topic, setTopic] = useState('');
  const [niche, setNiche] = useState('fashion');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [content, setContent] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Load last 10 ideas on mount
    const fetchHistory = async () => {
      try {
        const response = await axios.get('/api/ai/history');
        setHistory(response.data.data || []);
      } catch (err) {
        console.error('Failed to load history:', err);
      }
    };
    fetchHistory();
  }, []);

  const handleGenerate = async () => {
    if (!topic.trim()) {
      setError('Please enter a topic');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await axios.post('/api/ai/generate', { topic, niche });
      setContent(response.data.data);

      // Add new result to top of history list
      setHistory((prev) => [response.data.data, ...prev.slice(0, 9)]);
    } catch (err) {
      setError('Failed to generate content ideas. Please try again.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" gutterBottom>Content Idea Generator</Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 4, maxWidth: 800, mx: 'auto' }}>
        <Typography variant="h6" gutterBottom>Generate Instagram Content Ideas</Typography>

        <TextField
          fullWidth label="Enter your topic" value={topic}
          onChange={(e) => setTopic(e.target.value)} sx={{ mb: 3 }}
          placeholder="e.g., morning habits, home workouts"
        />

        <Select fullWidth value={niche} onChange={(e) => setNiche(e.target.value)} sx={{ mb: 3 }}>
          {niches.map((n) => (
            <MenuItem key={n} value={n}>{n.charAt(0).toUpperCase() + n.slice(1)}</MenuItem>
          ))}
        </Select>

        <Button 
          variant="contained" onClick={handleGenerate}
          disabled={isLoading} size="large" fullWidth
        >
          {isLoading ? <CircularProgress size={24} /> : 'Generate Content Ideas'}
        </Button>

        {error && (
          <Typography color="error" sx={{ mt: 2 }}>{error}</Typography>
        )}
      </Paper>

      {content && (
        <Card sx={{ mb: 4, maxWidth: 800, mx: 'auto' }}>
          <CardContent>
            <Typography variant="h5" gutterBottom>Your Content Ideas</Typography>
            <IdeaCard idea={content} />
          </CardContent>
        </Card>
      )}

      {/* History Section */}
      <Box sx={{ maxWidth: 800, mx: 'auto' }}>
        <Typography variant="h5" gutterBottom>Recent Generated Ideas</Typography>
        {history.length === 0 ? (
          <Typography color="text.secondary">No ideas generated yet.</Typography>
        ) : (
          history.map((idea, idx) => (
            <Card key={idx} sx={{ mb: 2 }}>
              <CardContent>
                <IdeaCard idea={idea} />
              </CardContent>
            </Card>
          ))
        )}
      </Box>
    </Box>
  );
};

const IdeaCard = ({ idea }) => (
  <Grid container spacing={2}>
    <Grid item xs={12}>
      <Typography variant="subtitle1" fontWeight="bold">Hook:</Typography>
      <Paper sx={{ p: 2, backgroundColor: '#f5f5f5' }}>{idea.hook}</Paper>
    </Grid>
    <Grid item xs={12}>
      <Typography variant="subtitle1" fontWeight="bold">Reel Idea:</Typography>
      <Paper sx={{ p: 2, backgroundColor: '#f5f5f5' }}>{idea.reelIdea}</Paper>
    </Grid>
    <Grid item xs={12}>
      <Typography variant="subtitle1" fontWeight="bold">Caption:</Typography>
      <Paper sx={{ p: 2, backgroundColor: '#f5f5f5' }}>{idea.caption}</Paper>
    </Grid>
    <Grid item xs={12}>
      <Typography variant="subtitle1" fontWeight="bold">Hashtags:</Typography>
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
        {idea.hashtags.map((tag, i) => (
          <Chip key={i} label={`#${tag}`} variant="outlined" />
        ))}
      </Box>
    </Grid>
  </Grid>
);

export default ContentIdeaGenerator;
